import React, { Component } from "react";
import axios from "axios";
import dotenv from "dotenv";
import ResultDetails from './ResultDetails'

dotenv.config();
let key = process.env.REACT_APP_API_KEY;


const napsterAPI = 'https://api.napster.com';
const APIKEY = '21db4d3b609445ad815433c940f86e02';
const oauthURL = `${napsterAPI}/oauth/authorize?client_id=${APIKEY}&response_type=code`;

const REDIRECT_URI = 'https://developer.napster.com/jsfiddle_proxy';

function fetchUserData (accessToken) {
	return $.ajax({
  	url: `${napsterAPI}/v2.1/me`,
    headers: {
      'Authorization': 'Bearer ' + accessToken
    }
  });	
}

function login() {
	window.addEventListener('message',(event) => {
    var hash = JSON.parse(event.data);
    if (hash.type === 'access_token') {
      fetchUserData(hash.access_token)
      	.then((data) => {
        	$loginSection.hide();
          $result.html(resultsTemplate(data.me));
          $result.show();
        });
    }
  }, false);

	window.open(
  	`${oauthURL}&redirect_uri=${REDIRECT_URI}`,
  	'Napster',
    `menubar=no,location=no,resizable=no,scrollbars=no,status=no,width=${width},height=${height},top=${top}, left=${left}`
  );
}

class Input extends Component {
  constructor(props) {
    super(props);

    this.state = {
      inputValue: "",
      allResults: []
    };
  }

  handleChange = evt => {
    this.setState({
      inputValue: evt.currentTarget.value
    });
  };

    handleSubmit = (evt) => {
    evt.preventDefault()
    const url = `http://api.napster.com/v2.2/search/verbose?apikey=NzAxMjI3ZTctZDAwMi00YWZhLWExODYtMWI0YWMwZGJjMmU5&query=drake`
    http://api.napster.com/v2.2/search/verbose?apikey=YTkxZTRhNzAtODdlNy00ZjMzLTg0MWItOTc0NmZmNjU4Yzk4&query=weezer+blue+album
    //  const url = `http://ws.audioscrobbler.com/2.0/?method=track.search&track=${this.state.inputValue}&api_key=${key}&format=json`
    axios.get(url).then(res => 
        console.log(res)
        // this.setState ({
        //     allResults: res.data.results.trackmatches.track,
        //     inputValue: ""
        // })
        )
        // const urlNapster = `http://api.napster.com/v2.2/artists/Art.28463069/images?apikey=${21db4d3b609445ad815433c940f86e02}`
    };

    render() {
        return (
            <div>
        <div className="input">
          <h1>Title of App</h1>
          <form onSubmit={this.handleSubmit}>
            <input
              type="text"
              name="song"
              value={this.state.inputValue}
              placeholder="Search for a song or artist"
              onChange={this.handleChange}
              />
            <button type="submit">ENTER</button>
          </form>
        </div>
        <div className="song-results">
            <h2>{(this.state.inputValue).toUpperCase()}</h2>
      {this.state.allResults.map((result, i) => 
             <ResultDetails result={result} index={i} />)}
        </div>
      </div>
    );
  }
}

export default Input;
