import React, { Component } from "react";
import axios from "axios";
import "./Components.css";

class Itunes extends Component {
  constructor(props) {
    super(props);

    this.state = {
      preview: ""
    };
  }

  componentDidMount = () => {
    axios.get(`https://itunes.apple.com/search?term=drake`).then(res =>
    // console.log(res.data.results[0].previewUrl)
      this.setState({
        preview: res.data.results[0].travk
      })
      );
    };

    render() {
        // console.log(this.state.preview)
    return (
    <div>
        <video controls autoPlay="" name="media"src={this.state.preview} type="audio/x-m4a">
        </video>
    </div>
    );
  }
}

export default Itunes;
