import React, { Component } from "react";
import {Link} from 'react-router-dom'

class ResultDetails extends Component {
    constructor(props){
        super(props)
    }

    setRedirect = () => {
        this.setState({
          redirect: true
        })
      }
      renderRedirect = () => {
        if (this.state.redirect) {
          return <Redirect to='/target' />
        }
      }

      render () {
        return (
           <div>
            {this.renderRedirect()}
            <button onClick={this.setRedirect}>Redirect</button>
           </div>
        )
      }

  render() {

    return (
      <div key={this.props.index}>
       <h4>{this.props.result.name}</h4>
        <h5 onClick={this.setRedirect}>{this.props.result.artist}</h5>
        <p>
          Listeners: {Number(this.props.result.listeners).toLocaleString("en")}
        </p>
        <a href={this.props.result.url}>last.fm Profile</a>
      </div>
    );
  }
}

export default ResultDetails;

